    }

    return (
        <div className="request-container">
            <div className="row">
                <div className="col-xs-6">
                    <form onSubmit={addRequest}>
                        <label>Request an image:</label>
                        <input type="text" placeholder="Enter a phrase or description..." onChange={handleInput} />
                        <input type="submit"/>
